import { useState } from 'react';

const useToggle = (initialValue = false) => {
  const {
    0: isOpen,
    1: setIsOpen
  } = useState(initialValue);

  const toggle = ev => {
    ev.preventDefault();
    setIsOpen(value => !value);
  };

  return [isOpen, toggle];
};

export default useToggle;
